import {
  ChangeDetectionStrategy,
  ChangeDetectorRef,
  Component,
  ViewEncapsulation,
  forwardRef,
  input,
  output,
  computed,
  signal,
  inject
} from '@angular/core';
import { NG_VALUE_ACCESSOR, ControlValueAccessor } from '@angular/forms';
import { CommonModule } from '@angular/common';
import { Icon, IconConfig } from '../../../icon/src/lib/icon.types';
import { IconModule } from '../../../icon/src/lib/icon.module';
import confetti from 'canvas-confetti';

let ratingCount = 1;

/** Event object emitted by RatingComponent. */
export class RatingEvent {
  source: RatingComponent;
  value: number;

  constructor(source: RatingComponent, value: number) {
    this.source = source;
    this.value = value;
  }
}

const SOL_RATING_VALUE_ACCESSOR = {
  provide: NG_VALUE_ACCESSOR,
  useExisting: forwardRef(() => RatingComponent),
  multi: true
};

@Component({
  selector: 'sol-rating',
  standalone: true,
  imports: [CommonModule, IconModule],
  templateUrl: './rating.component.html',
  styleUrls: ['./rating.component.scss'],
  providers: [SOL_RATING_VALUE_ACCESSOR],
  encapsulation: ViewEncapsulation.None,
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class RatingComponent implements ControlValueAccessor {
  protected changeDetectorRef = inject(ChangeDetectorRef);

  private readonly _uniqueId: number;
  private _id = '';

  /** The maximum rating value. Default is 5, cannot be more than 10. */
  readonly max = input<number>(5);

  /** Whether the rating is disabled. */
  readonly disabled = input<boolean>(false);
  private readonly _formDisabled = signal<boolean>(false);
  readonly isDisabled = computed(() => this.disabled() || this._formDisabled());

  /** The current rating value. */
  private readonly _value = signal<number>(0);
  readonly value = computed(() => this._value());

  /** The currently focused star index for keyboard navigation. */
  private readonly _focusedStarIndex = signal<number>(1);

  /** Whether the rating is read-only. */
  readonly readOnly = input<boolean>(false);

  /** The name attribute for the rating. */
  readonly name = input<string>();

  /** The aria-label for the rating. */
  readonly ariaLabel = input<string>();

  /** The aria-labelledby for the rating. */
  readonly ariaLabelledBy = input<string>();

  /** The aria-describedby for the rating. */
  readonly ariaDescribedBy = input<string>();

  /** Custom icon to use instead of the default star. */
  readonly customIcon = input<Icon>();

  /** Custom fill color to override the default green/gray colors. */
  readonly fillColor = input<string>();

  /** Whether to show confetti animation when max rating is selected. */
  readonly showConfetti = input<boolean>(true);

  /** Event emitted when the rating value changes. */
  readonly valueChange = output<RatingEvent>();

  /** The validated maximum value (5 by default, capped at 10). */
  readonly validatedMax = computed(() => {
    const maxValue = this.max();
    return Math.min(Math.max(1, maxValue), 10);
  });

  /** Array of star indices for template iteration. */
  readonly starIndices = computed(() => {
    return Array.from({ length: this.validatedMax() }, (_, i) => i + 1);
  });

  /** Whether to use custom icon instead of default star. */
  readonly useCustomIcon = computed(() => {
    return !!this.customIcon();
  });

  /** Gets the icon configuration for a specific star. */
  getIconConfig(starIndex: number): IconConfig {
    const isStarFilled = this.isStarFilled(starIndex);
    const customColor = this.fillColor();

    let fillColor: string;
    if (customColor) {
      fillColor = isStarFilled ? customColor : '#d1d5db';
    } else {
      fillColor = isStarFilled ? '#22c55e' : '#d1d5db';
    }

    return {
      icon: this.customIcon()!,
      className: 'sol-rating-custom-icon',
      width: '1.25rem',
      height: '1.25rem',
      fill: fillColor
    };
  }

  /** The component ID. */
  get ratingId(): string {
    if (!this._id) {
      return `sol-rating-${this._uniqueId}`;
    }
    return this._id;
  }

  set ratingId(id: string) {
    this._id = id;
  }

  constructor() {
    this._uniqueId = ratingCount++;
  }

  /** Method set in registerOnChange to propagate changes back to the form. */
  private propagateChange = (_: number) => {};

  /** Called when rating is blurred. Needed to properly implement ControlValueAccessor. */
  private _onTouched: () => void = () => {};

  /** Writes a value from ngModel to the component. */
  writeValue(value: number): void {
    this._value.set(value || 0);
    this.changeDetectorRef.markForCheck();
  }

  /** Sets a method in order to propagate changes back to the form. */
  registerOnChange(fn: (_: number) => void): void {
    this.propagateChange = fn;
  }

  /** Registers a callback to be triggered when the control has been touched. */
  registerOnTouched(fn: () => void): void {
    this._onTouched = fn;
  }

  /** ControlValueAccessor method to programmatically disable the rating. */
  setDisabledState(isDisabled: boolean) {
    this._formDisabled.set(isDisabled);
    this.changeDetectorRef.markForCheck();
  }

  /** Determines if a star should be filled based on the current value. */
  isStarFilled(starIndex: number): boolean {
    return starIndex <= this.value();
  }

  /** Handles star click events. */
  onStarClick(starIndex: number): void {
    if (this.isDisabled() || this.readOnly()) {
      return;
    }

    const previousValue = this._value();
    this._value.set(starIndex);
    this.propagateChange(starIndex);
    this.valueChange.emit(new RatingEvent(this, starIndex));
    this._onTouched();

    // Trigger confetti if max rating is selected and it wasn't already at max
    if (this.showConfetti() && starIndex === this.validatedMax() && previousValue < this.validatedMax()) {
      this.triggerConfetti();
    }

    this.changeDetectorRef.markForCheck();
  }

  /** Handles star keydown events for accessibility. */
  onStarKeydown(event: KeyboardEvent, starIndex: number): void {
    if (this.isDisabled()) {
      return;
    }

    switch (event.key) {
      case 'Enter':
      case ' ':
        event.preventDefault();
        if (!this.readOnly()) {
          this.onStarClick(starIndex);
        }
        break;
      case 'ArrowRight':
      case 'ArrowUp':
        event.preventDefault();
        this.moveFocus(Math.min(starIndex + 1, this.validatedMax()));
        break;
      case 'ArrowLeft':
      case 'ArrowDown':
        event.preventDefault();
        this.moveFocus(Math.max(starIndex - 1, 1));
        break;
      case 'Home':
        event.preventDefault();
        this.moveFocus(1);
        break;
      case 'End':
        event.preventDefault();
        this.moveFocus(this.validatedMax());
        break;
    }
  }

  /** Moves focus to a specific star. */
  private moveFocus(starIndex: number): void {
    this._focusedStarIndex.set(starIndex);
    // Focus the actual DOM element
    const starElements = document.querySelectorAll(`#${this.ratingId} .sol-rating-star`);
    const targetStar = starElements[starIndex - 1] as HTMLElement;
    if (targetStar) {
      targetStar.focus();
    }
  }

  /** Gets the aria-label for a specific star. */
  getStarAriaLabel(starIndex: number): string {
    if (this.ariaLabel()) {
      return `${this.ariaLabel()}: ${starIndex} of ${this.validatedMax()} stars`;
    }
    return `${starIndex} of ${this.validatedMax()} stars`;
  }

  /** Gets the tabindex for a specific star. */
  getStarTabIndex(starIndex: number): string {
    // The focused star should have tabindex="0", others should have tabindex="-1"
    // If no star is specifically focused, the first star should be focusable
    const focusedIndex = this._focusedStarIndex();
    return starIndex === focusedIndex ? '0' : '-1';
  }

  /** Handles keydown events on the container for number key shortcuts. */
  onContainerKeydown(event: KeyboardEvent): void {
    if (this.isDisabled() || this.readOnly()) {
      return;
    }

    // Handle number keys 0-9
    if (event.key >= '0' && event.key <= '9') {
      event.preventDefault();
      const ratingValue = parseInt(event.key, 10);

      // Key '0' sets rating to 0, keys '1'-'9' set rating to that number
      // But don't allow ratings higher than the max value
      if (ratingValue === 0) {
        this._value.set(0);
        this.propagateChange(0);
        this.valueChange.emit(new RatingEvent(this, 0));
        this._onTouched();
        this.changeDetectorRef.markForCheck();
      } else if (ratingValue <= this.validatedMax()) {
        const previousValue = this._value();
        this._value.set(ratingValue);
        this.propagateChange(ratingValue);
        this.valueChange.emit(new RatingEvent(this, ratingValue));
        this._onTouched();

        // Trigger confetti if max rating is selected and it wasn't already at max
        if (this.showConfetti() && ratingValue === this.validatedMax() && previousValue < this.validatedMax()) {
          this.triggerConfetti();
        }

        this.changeDetectorRef.markForCheck();

        // Update focus to the selected star
        this._focusedStarIndex.set(ratingValue);
      }
      // If ratingValue > validatedMax(), do nothing (ignore the key press)
    }
  }

  /** Triggers confetti animation when max rating is achieved. */
  private triggerConfetti(): void {
    confetti({
      particleCount: 100,
      spread: 70,
      origin: { y: 0.6 },
      colors: ['#22c55e', '#ffd700', '#ff6b6b', '#3b82f6', '#8b5cf6']
    });
  }
}

