import { ChangeDetectionStrategy, ChangeDetectorRef, Component, Input, OnChanges, TemplateRef, ViewEncapsulation } from '@angular/core';
import { Observable, catchError, lastValueFrom, of, retry, take } from 'rxjs';
import { HttpClient } from '@angular/common/http';
import { Icon, IconConfig } from './icon.types';

@Component({
  selector: 'sol-icon',
  templateUrl: './icon.component.html',
  styleUrls: ['./icon.component.scss'],
  encapsulation: ViewEncapsulation.None,
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class IconComponent {
  public htmlText = '';
  public templateRef?: any;

  configProperties: Array<string> = ['icon', 'className', 'fill', 'height', 'width', 'style', 'ariaLabel', 'ariaHidden'];
  @Input('icon') _input_icon: Icon = '';
  @Input('className') _input_className = '';
  @Input('height') _input_height = '';
  @Input('width') _input_width = '';
  @Input('fill') _input_fill = '';
  @Input('style') _input_style = '';
  @Input('ariaLabel') _input_ariaLabel = '';
  @Input('ariaHidden') _input_ariaHidden = false;
  // NOTES FOR NEW INPUTS:
  // 1. The internal name should be the public name prefixed by '_input_'.
  // 2. The public name should be added to the configProperties array.

  private predefinedIcons: any = {};
  private cachedIcons: any = {};

  config: IconConfig = { icon: '' };

  constructor(
    private httpClient: HttpClient,
    private cdr: ChangeDetectorRef
  ) {
    // icon names must not have a period (.) or a hash (#)
    this.predefinedIcons = {
      'sol-plus':
        '<svg viewBox="0 1 20 20" fill="currentColor" xmlns="http://www.w3.org/2000/svg"><path d="M18 9H11V2H9V9H2V11H9V18H11V11H18V9Z" /></svg>',
      'sol-dropdown-arrow':
        '<svg viewBox="3 3 14 14" fill="currentColor" xmlns="http://www.w3.org/2000/svg"><path d="M13.2653 7.30508L10 10.5593L6.73469 7.30508C6.32653 6.89831 5.71429 6.89831 5.30612 7.30508C4.89796 7.71186 4.89796 8.32203 5.30612 8.72881L9.28571 12.6949C9.38775 12.7966 9.4898 12.8983 9.59184 12.8983C9.69388 13 9.89796 13 10 13C10.102 13 10.3061 13 10.4082 12.8983C10.5102 12.7966 10.6122 12.7966 10.7143 12.6949L14.6939 8.72881C15.102 8.32203 15.102 7.71186 14.6939 7.30508C14.2857 6.89831 13.6735 6.89831 13.2653 7.30508Z" /></svg>',
      'sol-dots-vertical':
        '<svg viewBox="3 2 20 20" fill="currentColor" xmlns="http://www.w3.org/2000/svg"><path fill-rule="evenodd" clip-rule="evenodd" d="M10 5C10 3.89543 10.8954 3 12 3C13.1046 3 14 3.89543 14 5C14 6.10457 13.1046 7 12 7C10.8954 7 10 6.10457 10 5ZM10 12C10 10.8954 10.8954 10 12 10C13.1046 10 14 10.8954 14 12C14 13.1046 13.1046 14 12 14C10.8954 14 10 13.1046 10 12ZM10 19C10 17.8954 10.8954 17 12 17C13.1046 17 14 17.8954 14 19C14 20.1046 13.1046 21 12 21C10.8954 21 10 20.1046 10 19Z" /></svg>',
      'sol-calendar':
        '<svg viewBox="0 0 16 16" fill="currentColor" xmlns="http://www.w3.org/2000/svg"><path fill-rule="evenodd" d="M14 2H2a1 1 0 0 0-1 1v11a1 1 0 0 0 1 1h12a1 1 0 0 0 1-1V3a1 1 0 0 0-1-1ZM2 1a2 2 0 0 0-2 2v11a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V3a2 2 0 0 0-2-2H2Z" clip-rule="evenodd"/><path d="M8.5 6.5h2v2h-2v-2Zm0 3h2v2h-2v-2Zm0 3h2v2h-2v-2Zm-3 0h2v2h-2v-2Zm-3 0h2v2h-2v-2Zm3-3h2v2h-2v-2Zm0-3h2v2h-2v-2Zm-3 3h2v2h-2v-2Zm9-3h2v2h-2v-2Zm0 3h2v2h-2v-2Z"/><path fill-rule="evenodd" d="M15 6H1V4h14v2ZM4.5 3V0h1v3h-1Zm6 0V0h1v3h-1Z" clip-rule="evenodd"/></svg>',
      'sol-square':
        '<svg viewBox="0 0 20 20" fill="currentColor" xmlns="http://www.w3.org/2000/svg"><path d="M 16 0 H 4 C 1.8 0 0 1.8 0 4 V 16 C 0 18.2 1.8 20 4 20 H 16 C 18.2 20 20 18.2 20 16 V 4 C 20 1.8 18.2 0 16 0 V 0 Z Z"/></svg>'
    };
  }

  async ngOnChanges() {
    if (!this._input_icon) {
      return;
    }

    this.prepareConfig();

    // STRINGS
    // -------
    // Name: sol-plus
    // SVG: ./path/single.svg
    // SVG Sprite: ./path/sprite.svg#my-icon
    // SVG Sprite Symbol: #my-icon

    if (typeof this.config.icon === 'string') {
      const hashCount = (this.config.icon.match(/\#/g) || []).length;
      const dotSvgCount = (this.config.icon.match(/\.svg/g) || []).length;

      let tempString = '';

      // PREDEFINED ICON
      if (this.predefinedIcons[this.config.icon]) {
        tempString = this.predefinedIcons[this.config.icon];

        // SVG SPRITE
      } else if (hashCount === 1) {
        tempString = this.getStringForSvgSprite(this.config.icon);

        // SINGLE SVG
      } else if (dotSvgCount === 1) {
        // Get the SVG from the cache, if available
        if (this.cachedIcons[this.config.icon]) {
          tempString = this.cachedIcons[this.config.icon];
        } else {
          await this.fetchSingleSVG(this.config.icon).then(svgText => {
            tempString = svgText;
          });
        }
      }

      // ADD CLASS, STYLE, AND ARIA LABEL ATTRIBUTES
      tempString = this.addAttributes(tempString);
      this.templateRef = undefined;
      this.htmlText = tempString;
    }

    // TEMPLATEREF
    if (this.config.icon instanceof TemplateRef) {
      this.htmlText = '';
      this.templateRef = this.config.icon;
    }

    this.cdr.detectChanges();
  }

  private getStringForSvgSprite(icon: string) {
    const iconSpriteAndName = icon.split('#');
    const spritePath = iconSpriteAndName[0];
    const iconName = iconSpriteAndName[1];

    if (iconName) {
      const el = document.querySelector(`symbol#${iconName}`);
      return `<svg><use href="${!el ? spritePath : ''}#${iconName}" ></use></svg>`;
    }

    return '';
  }

  private addAttributes(tempString: string) {
    // ADD CLASS ATTRIBUTE
    if (this.config.className) {
      tempString = tempString.replace('<svg', `<svg class="${this.config.className}"`);
      tempString = tempString.replace('<img', `<img class="${this.config.className}"`);
    }

    // ADD ALL STYLE-RELATED ATTRIBUTES
    if (this.config.style || this.config.height || this.config.width || this.config.fill) {
      // add a style tag if any style-related attributes are present
      tempString = tempString.replace('<svg', '<svg style=""');
      tempString = tempString.replace('<img', '<img style=""');

      if (this.config.style) {
        this.config.style = this.config.style.replace(/;$/, '') + ';'; // add a semi-colon, if one is not present
        tempString = tempString.replace('style=""', `style="${this.config.style}"`);
      }

      if (this.config.height) {
        tempString = tempString.replace('style="', `style="height:${this.config.height};`);
      }

      if (this.config.width) {
        tempString = tempString.replace('style="', `style="width:${this.config.width};`);
      }

      if (this.config.fill) {
        tempString = tempString.replace('style="', `style="fill:${this.config.fill};`);
      }
    }

    // ADD ARIA LABEL AS ALT ATTRIBUTE IN IMG TAGS
    if (this.config.ariaLabel) {
      tempString = tempString.replace('<img', `<img alt="${this.config.ariaLabel}"`);
    }

    return tempString;
  }

  // The icon input can be a single string or TemplateRef, or it can be a configuration object containing
  // all of the inputs. It can also be a combination of both. (Note: The configuration object will take
  // precedence with the following exceptions: the `className` and `style` properties will be combined.)
  // Here we map the inputs to this.config to handle these two different options.
  private prepareConfig() {
    let inputsDirect: Array<any> = [];
    let inputsIconConfig: Array<any> = [];

    /* *** START WITH PROPERTIES FROM INPUTS *** */
    // Object.entries creates an array of arrays. For each element in the main array,
    // item[0] is the name of the property and item[1] is the value of that property
    inputsDirect = Object.entries(this)
      .filter(item => item[0].includes('_input_'))
      .map(item => [item[0].replace('_input_', ''), item[1]]);

    inputsDirect.forEach(item => {
      if (this.configProperties.includes(item[0])) {
        (this.config as any)[item[0]] = item[1];
      }
    });

    /* *** THEN USE PROPERTIES FROM ICON CONFIGURATION OBJECT INSTEAD OR IN COMBINATION *** */
    if (this._input_icon && this._input_icon.hasOwnProperty('icon')) {
      // Object.entries creates an array of arrays. For each element in the main array,
      // item[0] is the name of the property and item[1] is the value of that property
      inputsIconConfig = Object.entries(this._input_icon);

      inputsIconConfig.forEach(item => {
        if (this.configProperties.includes(item[0])) {
          if (['className', 'style'].includes(item[0])) {
            let separator = ' ';
            if (item[0] === 'style' && this.config.style) separator = ';';
            // combine className and style from direct inputs and the icon config object
            (this.config as any)[item[0]] = ((this.config as any)[item[0]] + separator + item[1]).trim();
          } else {
            // for all other properties, use the icon config instead of the direct inputs
            (this.config as any)[item[0]] = item[1];
          }
        }
      });
    }
  }

  async fetchSingleSVG(url: string): Promise<any> {
    // Make the HTTP call to fetch the SVG
    const request$ = this.httpClient.get(url, { responseType: 'text' }).pipe(
      retry(1),
      take(1),
      catchError((error: any, caught: Observable<any>): Observable<any> => {
        // if the SVG cannot be retrieved with get(), then load it through <img>
        return of(`<img src="${url}">`);
      })
    );
    const svgText = await lastValueFrom<any>(request$);
    this.cachedIcons[url] = svgText;
    return svgText;
  }
}

