  icon: Icon;
  className?: string;
  height?: string;
  width?: string;
  fill?: string;
  style?: string;
  ariaLabel?: string;
  ariaHidden?: string;
}

@if (htmlText) {
  <div
    class="sol-icon"
    [attr.aria-hidden]="config.ariaHidden ? true : undefined"
    [attr.aria-label]="config.ariaLabel ? config.ariaLabel : undefined"
    role="img"
    [innerHtml]="htmlText | sanitizeHtml"
  ></div>
}
@if (templateRef) {
  <ng-container *ngTemplateOutlet="templateRef"></ng-container>
}

