import { ComponentFixture, TestBed, tick } from '@angular/core/testing';
import { IconModule } from './icon.module';
import { IconComponent } from './icon.component';
import { of, throwError } from 'rxjs';

describe('Icon', () => {
  let component: IconComponent;
  let fixture: ComponentFixture<IconComponent>;

  beforeEach(async () => {
    await TestBed.configureTestingModule({
      declarations: [IconComponent],
      imports: [IconModule]
    }).compileComponents();
    fixture = TestBed.createComponent(IconComponent);
    component = fixture.componentInstance;
  });

  it('should handle a prefined icon name', () => {
    component._input_icon = 'sol-plus';
    component.ngOnChanges();
    expect(component.htmlText).toEqual((component as any).predefinedIcons['sol-plus']);
  });

  it('should handle an SVG string', () => {
    const svgString = '<svg viewBox="0 0 100 100" xmlns="http://www.w3.org/2000/svg"><circle r="45" cx="50" cy="50" fill="red" /></svg>';
    component._input_icon = svgString;
    component.ngOnChanges();
    expect(component.htmlText).toEqual(svgString);
  });

  it('should handle an SVG string with extra whitespace', () => {
    const svgString = '  <svg viewBox="0 0 100 100" xmlns="http://www.w3.org/2000/svg"><circle r="45" cx="50" cy="50" fill="red" /></svg>  ';
    component._input_icon = svgString;
    component.ngOnChanges();
    expect(component.htmlText).toEqual(svgString);
  });

  it('should handle an SVG string with attributes applied', () => {
    const svgString = '<svg xmlns="http://www.w3.org/2000/svg"><circle r="45" cx="50" cy="50" fill="red" /></svg>';
    component._input_icon = {
      icon: svgString,
      className: 'custom-class',
      style: 'margin: 10px;',
      height: '80px',
      width: '80px'
    };
    component.ngOnChanges();
    expect(component.htmlText).toContain('class="custom-class"');
    expect(component.htmlText).toContain('style="width:80px;height:80px;margin: 10px;"');
    expect(component.htmlText).toContain('<circle r="45" cx="50" cy="50" fill="red" />');
  });

  it('should not treat partial SVG strings as SVG strings', () => {
    component._input_icon = '<svg>incomplete';
    component.ngOnChanges();
    expect(component.htmlText).toEqual('');
  });

  it('should handle multiline SVG strings', () => {
    const svgString = `<svg height="100" width="100" xmlns="http://www.w3.org/2000/svg">
      <circle r="45" cx="50" cy="50" fill="red" />
    </svg>`;
    component._input_icon = svgString;
    component.ngOnChanges();
    expect(component.htmlText).toEqual(svgString);
  });

  it('should handle an SVG sprite', () => {
    component._input_icon = './path/file.svg#icon';
    component.ngOnChanges();
    expect(component.htmlText).toContain('<svg');
    expect(component.htmlText).toContain('<use');
    expect(component.htmlText).toContain('href="' + component._input_icon);
  });

  it('should handle an SVG sprite with no icon name', () => {
    component._input_icon = './path/file.svg#';
    component.ngOnChanges();
    expect(component.htmlText).toEqual('');
  });

  it('should handle a single SVG', async () => {
    spyOn((component as any).httpClient, 'get').and.returnValue(of('<svg></svg>'));
    component._input_icon = './path/file.svg';
    await component.ngOnChanges();
    expect(component.htmlText).toContain('<svg></svg>');
  });

  it('should cache a single SVG', async () => {
    spyOn((component as any).httpClient, 'get').and.returnValues(of('<svg id="svg1"></svg>'), of('<svg id="svg2"></svg>'));
    component._input_icon = './path/file1.svg';
    await component.ngOnChanges();
    component._input_icon = { icon: './path/file2.svg' };
    await component.ngOnChanges();
    component._input_icon = { icon: './path/file1.svg' };
    await component.ngOnChanges();
    expect((component as any).cachedIcons).toEqual({
      './path/file1.svg': '<svg id="svg1"></svg>',
      './path/file2.svg': '<svg id="svg2"></svg>'
    });
    expect(component.htmlText).toContain('<svg id="svg1"></svg>');
  });

  it('should handle a single SVG with an error', async () => {
    spyOn((component as any).httpClient, 'get').and.returnValue(throwError(() => new Error()));
    component._input_icon = './path/file.svg';
    await component.ngOnChanges();
    expect(component.htmlText).toContain('<img');
    expect(component.htmlText).toContain('src="' + component._input_icon);
  });

  it('should add a className', () => {
    component._input_icon = {
      icon: 'sol-plus',
      className: 'additional-class'
    };
    component.ngOnChanges();
    expect(component.htmlText).toContain('<svg class="additional-class"');
  });

  it('should add style and fill', () => {
    component._input_icon = 'sol-plus';
    component._input_fill = '#FFF';
    component._input_style = 'border: 1px solid red';
    component.ngOnChanges();
    expect(component.htmlText).toContain('<svg style="fill:#FFF;border: 1px solid red;"');
  });

  it('should add height and width', () => {
    component._input_icon = 'sol-plus';
    component._input_height = '60px';
    component._input_width = '60px';
    component.ngOnChanges();
    expect(component.htmlText).toContain('<svg style="width:60px;height:60px;"');
  });

  it('should add the ariaLabel as alt attribute for img tag', async () => {
    spyOn((component as any).httpClient, 'get').and.returnValue(throwError(() => new Error()));
    component._input_icon = {
      icon: './path/404.svg',
      ariaLabel: 'File'
    };
    await component.ngOnChanges();
    expect(component.htmlText).toContain('<img');
    expect(component.htmlText).toContain('src="' + component._input_icon.icon);
    expect(component.htmlText).toContain('alt="' + component._input_icon.ariaLabel);
  });

  it('should combine className and style properties from inputs and from icon config', () => {
    component._input_className = 'class-from-input';
    component._input_style = 'background: green';
    component._input_icon = {
      icon: 'sol-dots-vertical',
      className: 'class-from-config-object',
      style: 'border: 1px solid red'
    };
    component.ngOnChanges();
    expect(component.htmlText).toContain('class-from-input');
    expect(component.htmlText).toContain('class-from-config-object');
    expect(component.htmlText).toContain('background: green;');
    expect(component.htmlText).toContain('border: 1px solid red;');
  });

  it('should use all other properties from icon config instead of from inputs', () => {
    component._input_height = '200px';
    component._input_width = '100px';
    component._input_fill = '#FFF';
    component._input_icon = {
      icon: 'sol-dots-vertical',
      height: '50px',
      width: '25px',
      fill: 'yellow'
    };
    component.ngOnChanges();
    expect(component.htmlText).not.toContain('200px');
    expect(component.htmlText).not.toContain('100px');
    expect(component.htmlText).not.toContain('#FFF');
    expect(component.htmlText).toContain('fill:yellow');
    expect(component.htmlText).toContain('height:50px');
    expect(component.htmlText).toContain('width:25px');
  });

  it('should handle a falsy value for icon', async () => {
    component._input_icon = null;
    const x = await component.ngOnChanges();
    expect(x).toBeUndefined();
  });
});

