  Output,
  signal,
  ViewChild,
  ViewEncapsulation
} from '@angular/core';
import { ControlValueAccessor, NG_VALUE_ACCESSOR } from '@angular/forms';
import { Icon } from '@niceltd/sol/icon';
import { _IdGenerator } from '@niceltd/sol';

@Component({
  selector: 'sol-text-input',
  templateUrl: './text-input.component.html',
  styleUrls: ['./text-input.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
  encapsulation: ViewEncapsulation.None,
  providers: [
    {
      provide: NG_VALUE_ACCESSOR,
      useExisting: TextInputComponent,
      multi: true
    }
  ]
})
export class TextInputComponent implements ControlValueAccessor, OnInit {
  private readonly _uid: string;
  private readonly idGenerator = inject(_IdGenerator);
  constructor(
    private cdr: ChangeDetectorRef,
    private elementRef: ElementRef
  ) {
    this._uid = this.idGenerator.getId('sol-text-input-');
  }

  ngOnInit() {
    if ((this.value as any) === 0) {
      this.value = '0';
    }
  }

  @Input() label = '';
  @Input() placeholder = '';
  readonly disabled = input<boolean>(false);
  private readonly _formDisabled = signal<boolean>(false);
  readonly isDisabled = computed(() => this.disabled() || this._formDisabled());
  @Input() name = '';
  readonly id = input<string>('');
  readonly currentId = computed(() => this.id() || this._uid);
  @Input() required = false;
  @Input() readonly = false;
  @Input() autocomplete = 'off';
  @Input() errorMessageText: string | null = null;
  @Input('aria-label') ariaLabel = '';
  @Input('aria-labelledby') ariaLabelledby: string | null = null;
  @Input() icon?: Icon;
  @Input() iconEnd?: Icon;
  @Input() value = '';
  @Input() errorState = false;
  @Input() type: 'text' | 'password' | 'search' = 'text';
  @Input() maxCharLength: number | null = null;
  @Output() focusEvent = new EventEmitter();
  @Output() blurEvent = new EventEmitter();
  @Output() valueChange = new EventEmitter();
  @Output() highlightedText = new EventEmitter<string>();
  @ViewChild('textInput', { static: false }) textInput!: ElementRef;

  onInputContainerClick() {
    this.textInput.nativeElement.focus();
  }

  handleSelection() {
    const selection = document.getSelection();
    if (selection && selection.rangeCount > 0) {
      this.highlightedText.emit(selection.toString());
    }
  }

  writeValue(value: any) {
    if (value === 0) {
      value = '0';
    }
    if (value) {
      this.value = value;
    } else {
      this.value = '';
    }
    this.cdr.markForCheck();
  }

  /**
   * Registers a listener that notifies the model when the control updates
   */
  registerOnChange(fn: (_: string) => void) {
    this.onChangeHandler = fn;
  }

  /**
   * Registers a listener that notifies the model when the control is blurred
   */
  registerOnTouched(fn: () => void) {
    this._onTouchedHandler = fn;
  }

  setDisabledState(isDisabled: boolean): void {
    this._formDisabled.set(isDisabled);
  }

  /**
   * Handles the input event from the `input`.
   * Sends events to the change handler and emits a value.
   */
  onInput(event: Event) {
    this.value = (<HTMLInputElement>event.target).value;
    this.onChangeHandler(this.value);
    this.valueChange.emit(this.value);
  }

  onChange(event: Event) {
    event.stopPropagation();
  }
  /**
   * Listens for the host blurring, and notifies the model
   */
  @HostListener('focusout')
  focusOut() {
    this._onTouchedHandler();
  }

  /**
   * placeholder declarations. Replaced by the functions provided to `registerOnChange` and `registerOnTouched`
   */
  onChangeHandler = (_: string) => {};
  _onTouchedHandler = () => {};
}

