    letter-spacing: #{var(--typography-opensans-body-body4-letter-spacing)};
    font-stretch: #{var(--typography-opensans-body-body4-font-stretch)};
    text-decoration: #{var(--typography-opensans-body-body4-text-decoration)};
    margin-top: 0.4rem;
    display: block;
  }
