.sol-text-input {
  display: block;

  .read-only-label {
    color: #{var(--color-interface-text800)};
  }

  .sol-input-container {
    box-sizing: border-box;
    height: $input-container-height;
    padding: $input-container-padding;
    border-style: solid;
    border-width: #{var(--border-size1)};
    border-color: #{var(--color-globalstate-inactive-border)};
    border-radius: #{var(--radii-small-allcorners)};
    background-color: #fff;
    display: inline-flex;
    align-items: center;
    width: 100%;

    &:hover {
      border-color: #{var(--color-globalstate-hover-border)};
    }

    &:focus-within {
      // TODO update design tokens
      border-color: #00000080;
    }

    &.error-border {
      border-color: #{var(--color-button-destructive-hover)};

      &:active,
      &:focus {
        border-color: #{var(--color-button-destructive-hover)};
      }
    }

    sol-icon:first-child {
      padding-right: #{var(--gap8)};
    }

    sol-icon:nth-of-type(2) {
      padding-right: 0.45rem;
      padding-left: #{var(--gap8)};
    }

    svg {
      fill: #{var(--sol-color-icon-regular)};
    }
  }

  .disabled {
    outline: #{var(--color-globalstate-disabled-borderalt)};
    border-style: solid;
    border-width: #{var(--border-size1)};
    border-color: #{var(--color-globalstate-disabled-borderalt)};
    pointer-events: none;
    background-color: #{var(--color-globalstate-disabled-background)};

    svg {
      path {
        fill: #{var(--color-globalstate-disabled-border)};
      }
    }
  }

  .read-only {
    outline: #{var(--color-globalstate-disabled-borderalt)};
    border-style: solid;
    border-width: #{var(--border-size1)};
    border-color: #{var(--color-globalstate-disabled-borderalt)};
    color: #{var(--color-interface-text800)};
    cursor: default;
    background-color: #{var(--color-globalstate-disabled-background)};

    &:focus-within {
      border-color: #{var(--color-globalstate-disabled-borderalt)};
    }

    &:hover {
      border-color: #{var(--color-globalstate-disabled-borderalt)};
    }
  }

  label.sol-text-input-label {
    font-family: $label-font-family;
    font-style: #{var(--typography-opensans-body-overline-font-style)};
    font-weight: #{var(--typography-opensans-body-overline-font-weight)};
    font-size: #{var(--typography-opensans-body-overline-font-size)};
    line-height: $label-line-height;
    letter-spacing: #{var(--typography-opensans-body-overline-letter-spacing)};
    display: flex;
    align-items: center;
    text-transform: #{var(--typography-opensans-body-overline-text-case)};
    color: #{var(--color-interface-text700)};
    text-decoration: #{var(--typography-opensans-body-overline-text-decoration)};
    font-stretch: #{var(--typography-opensans-body-overline-font-stretch)};
    flex: none;
    order: 0;
    flex-grow: 0;
    margin-bottom: 0.25rem;

    .required-label {
      color: #{var(--color-button-destructive-hover)};
      padding-left: 0.25rem;
    }
  }

  .label-disabled {
    color: #{var(--color-globalstate-disabled-content)};
  }

  input.sol-input {
    height: 100%;
    width: 100%;
    border-style: none;
    border-width: 0;
    border-color: transparent;
    box-sizing: border-box;
    background-color: #fff;
    outline: none;
